// import bootstrapTable from 'react-bootstrap-table-next/lib/src/bootstrap-table';
// import BootstrapTable from 'react-bootstrap-table-next/lib/src/bootstrap-table';
// import axios from 'axios';
import React from "react";
import Actions from "./actions";

function Table(probs){
    // receiving the data for the table
    const data = probs.data;
    // receiving the headings of the table
    const columns = probs.column;
    return (
        <div className="d-block table-responsive">
            <table className="table table-striped table-hover table-bordered">
            <thead>
                <tr>
                {React.Children.toArray(columns.map((item,index) =><TableHeadItem item={item}/>))}
                </tr>
            </thead>
            <tbody>
                {React.Children.toArray(data.map((item,index) =><TableRow item={item} column={columns}/>))}
            </tbody>
            </table>
        </div>
    )
}
function TableHeadItem({item}){
    return <th>{item.heading}</th>
}
function TableRow({item , column}){
    return(
    <tr>
        {React.Children.toArray(column.map((columnItem , index)=> {
        return <td>{`${columnItem.heading}`==="Actions"? <Actions/>:
        `${columnItem.heading}`==="Photo"?<img src={item[`${columnItem.value}`]} style={{width:"200px" , height:"200px"}} className="img-fluid img-thumbnail" alt=""/>:item[`${columnItem.value}`]
        }</td>
    }))}
    </tr>)
}
export default Table;
