// import logo from './logo.svg';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import "../styles/ThreeTabs.css"
import Table from './Table';
import BooksTable from './BooksTable';
import AuthorsTable from './AuthorsTabel';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
// import res from 'express/lib/response';

function ThreeTabs() {
  const[retrieveddata,setData] = useState([]);
  
  // useEffect(()=>{axios.get('http://localhost:3001/categories')
  // .then(response => setData(response.data))
  // .catch(err => console.log(err))},[])
  // console.log(...retrieveddata);

    const viewCategories=()=> {axios.get('http://localhost:3001/categories')
                              .then(response => {setData(response.data);})};

    const columns = [
      { heading :"ID",value:'_id'},
      { heading: "Name",value:'name'},
      { heading: "Actions"}
    ]

    const viewBooks=()=>{
      console.log("books");
    }
  return (
    <div className="">
      {/* <Tabs
        defaultActiveKey="first"
        transition={false}
        id="threeTabs"
        className="tabs"
        >

        <Tab eventKey="first" title="Categories" on>
          <button className='categoryPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
          < Table data={retrieveddata} column={columns}/>
        </Tab>

        <Tab eventKey="second" title="Books">
          <button className='booksPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
          < BooksTable/>
        </Tab>

        <Tab eventKey="third" title="Authors" >
          <button className='authorsPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
          < AuthorsTable/>
        </Tab>
      </Tabs> */}

      <ul className="tabs nav nav-tabs" role="tablist">
        <li className="nav-item" role="presentation">
          <button type="button" id="threeTabs-tab-first" 
                  role="tab" data-rr-ui-event-key="first" 
                  aria-controls="threeTabs-tabpane-first" 
                  aria-selected="true" className="nav-link active">Categories</button>
        </li>
        <li className="nav-item" role="presentation">
          <button type="button" id="threeTabs-tab-second" 
                  role="tab" data-rr-ui-event-key="second" 
                  aria-controls="threeTabs-tabpane-second" 
                  tabindex="-1" className="nav-link">Books</button>
        </li>
        <li className="nav-item" role="presentation">
          <button type="button" id="threeTabs-tab-third" 
                  role="tab" data-rr-ui-event-key="third" 
                  aria-controls="threeTabs-tabpane-third" tabindex="-1" className="nav-link">Authors</button>
        </li>
      </ul>

      <div className="tab-content">
        <div role="tabpanel" id="threeTabs-tabpane-first" 
              aria-labelledby="threeTabs-tab-first" className="tab-pane active">
          <button className="categoryPlusBtn btn btn-success me-md-2 rounded-circle">
            <i className="fa fa-plus"></i></button>
          <Table/>
        </div>
        <div role="tabpanel" id="threeTabs-tabpane-second" 
            aria-labelledby="threeTabs-tab-second" className="tab-pane">
          <button className="booksPlusBtn btn btn-success me-md-2 rounded-circle">
            <i className="fa fa-plus"></i></button>
          <BooksTable/>
        </div>
        <div role="tabpanel" id="threeTabs-tabpane-third" 
            aria-labelledby="threeTabs-tab-third" className="tab-pane">
        <button className="authorsPlusBtn btn btn-success me-md-2 rounded-circle">
          <i className="fa fa-plus"></i></button>
          <AuthorsTable/>
        </div>
      </div>
    </div>
  );
}

export default ThreeTabs;
