import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import { useFormik } from 'formik';

function EditAuthorModal(probs){
  const item=probs.item;
  const formik = useFormik({
    initialValues:{
      fname :item.fName,
      lname:item.lName,
      dob:item.DOB,
      image:item.img
    },
    onSubmit:values=>{
      console.log(values);
      probs.onClick();
    }
    
  })

//   const handleChange=(event)=>{
//     const {name , value}=event.target;
//     setAddedAuthor(prevAuthor=>{
//     return {
//         ...prevAuthor,
//         [name]:value
//     }
//     })
// }
//   const handleClick=(event)=>{
//     event.preventDefault();
//     const newAuthor = {
//       _id:item._id,
//       fname :addedAuthor.fname,
//       lname:addedAuthor.lname,
//       dob:addedAuthor.dob,
//       image:addedAuthor.image
//     }
//     console.log(newAuthor);
//     probs.onClick();
//   }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Edit Author</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={formik.handleSubmit}>
            <Form.Group as={Row} className="mb-3" controlId="firstName">
                <Col sm={2}>
                    <Form.Label>First Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        autoComplete='off'
                        name='fname'
                        // value={addedAuthor.fname}
                        value={formik.values.fname}
                        onChange={formik.handleChange}
                        autoFocus/>                   
                      {formik.errors.fname?(<span className='error'>{formik.errors.fname}</span>):null}
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="lastName">
                <Col sm={2}>
                    <Form.Label>Last Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        autoComplete='off'
                        name='lname'
                        value={formik.values.lname}
                        onChange={formik.handleChange}
                        />
                        {formik.errors.lname?(<span className='error'>{formik.errors.lname}</span>):null}
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="dob">
                <Col sm={2}>
                    <Form.Label>Date of Birth</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="date"
                        name='dob'
                        value={formik.values.dob}
                        onChange={formik.handleChange}
                        />
                        {formik.errors.dob?(<span className='error'>{formik.errors.dob}</span>):null}
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        name='image'
                      />
                      {formik.errors.image?(<span className='error'>{formik.errors.image}</span>):null}
                </Col>
            </Form.Group>
            
            <div className='text-center'>
              <Button variant="primary" type="submit">
                Edit Author
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EditAuthorModal;
