import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import { useState } from 'react';
import axios from 'axios';
import { useFormik } from 'formik';

function AddCategoryModal(probs){
  const formik = useFormik({
    initialValues:{
      newCategory:''
    }
    
  })

  //state of the input 
  // const [input , setNewCategory] = useState({
  //   newCategory:''
  // });

  //to get the name of the new category when the input value changes
  const handleChange=(event)=>{
    const {name , value}=event.target;
    setNewCategory(prevCategory=>{
      return {
        ...prevCategory,
        [name]:value
      }
    })
  }

  //to post the new category in the database when the button is clicked
  const handleClick=(event)=>{
    event.preventDefault();
    console.log(input);
    const newCategory = {
      Name:input.newCategory
    }
    probs.onClick();
    
    axios.post('http://localhost:3001/categories',newCategory)
    .then(response => console.log(response))
    .catch(err => console.log(err.response))
  }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add Category</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group as={Row} className="mb-3" controlId="categoryName">
                <Col sm={3}>
                    <Form.Label>Category Name</Form.Label>
                </Col>
                <Col sm={9}>
                    <Form.Control
                        type="text"
                        name='newCategory'
                        value={input.newCategory}
                        autoComplete="off"
                        onChange={handleChange}
                        placeholder="Enter the Category Name"
                        autoFocus/>
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" onClick={handleClick} type="submit ">
                Add Category
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddCategoryModal;
