import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import { useState } from 'react';

function AddAuthorModal(probs){
  const item=probs.item;
  const [addedAuthor , setAddedAuthor] = useState({
    fname :item.fName,
    lname:item.lName,
    dob:item.DOB,
    image:item.img
  })

  const handleChange=(event)=>{
    const {name , value}=event.target;
    setAddedAuthor(prevAuthor=>{
    return {
        ...prevAuthor,
        [name]:value
    }
    })
}
const handleClick=(event)=>{
    event.preventDefault();
    const newBook = {
      name:addedAuthor.bookName,
      category:addedAuthor.category,
      author:addedAuthor.author,
      image:addedAuthor.image
    }
    console.log(newBook);
    probs.onClick();
  }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add Author</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group as={Row} className="mb-3" controlId="firstName">
                <Col sm={2}>
                    <Form.Label>First Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        placeholder="Enter the Author First Name"
                        autoComplete='off'
                        name='fname'
                        value={addedAuthor.fname}
                        onChange={handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="lastName">
                <Col sm={2}>
                    <Form.Label>Last Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        placeholder="Enter the Author Last Name"
                        autoComplete='off'
                        name='lnamw'
                        value={addedAuthor.lname}
                        onChange={handleChange}
                        />
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="dob">
                <Col sm={2}>
                    <Form.Label>Date of Birth</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="date"
                        name='dob'
                        value={addedAuthor.dob}
                        onChange={handleChange}
                        />
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        name='image'
                        />
                </Col>
            </Form.Group>
            
            <div className='text-center'>
              <Button variant="primary" type="submit" onClick={handleClick}>
                Add Author
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddAuthorModal;
