import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import React from 'react';
import { useFormik } from 'formik';

function EditBookModal(probs){
  const item=probs.item;
  const formik = useFormik({
    initialValues:{
      bookName:item.title,
      category:item.category,
      author:item.author,
      image:item.img
    },
    onSubmit:values=>{
      console.log(values);
      probs.onClick();
    }
    
  });
//   const handleChange=(event)=>{
//     const {name , value}=event.target;
//     setEditedBook(prevBook=>{
//     return {
//         ...prevBook,
//         [name]:value
//     }
//     })
// }
// const handleClick=(event)=>{
//     event.preventDefault();
//     const editBook = {
//       _id:item._id,
//       name:editedBook.bookName,
//       category:editedBook.category,
//       author:editedBook.author,
//       image:editedBook.image
//     }
//     console.log(editBook);
//     probs.onClick();
//   }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title  className='justify-content-center'>Edit Book</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={formik.handleSubmit}>
            <Form.Group as={Row} className="mb-3" controlId="bookName">
                <Col sm={2}>
                    <Form.Label>Book Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        autoComplete='off'
                        name='bookName'
                        // value={editedBook.bookName}
                        value={formik.values.bookName}
                        onChange={formik.handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="category">
                <Col sm={2}>
                    <Form.Label>Category</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='category'
                                     value={formik.values.category}
                                     onChange={formik.handleChange}>
                    {React.Children.toArray(probs.categriesData.map((item )=> {
                      return <option>{item['Name'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="author">
                <Col sm={2}>
                    <Form.Label>Author</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='author'
                                     value={formik.values.author}
                                     onChange={formik.handleChange}>
                    {React.Children.toArray(probs.authorData.map((item )=> {
                      return <option>{item['fName'].toString() +' '+ item['lName'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        name='image'
                        />
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" type="submit">
                Edit Book
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EditBookModal;
