// import logo from './logo.svg';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import "../styles/ThreeTabs.css"
import Table from './Table';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import AddCategoryModal from './addCategoryModal';
import AddBookModal from './addBookModal';
import AddAuthorModal from './addAuthorModal';

function ThreeTabs() {
  const[categoriesData,setCategoriesData] = useState([]);
  const[booksData,setBooksData]=useState([]);
  const[authorsData,setAuthorsData] = useState([]);

  const categoryColumns = [
    { heading :"ID",value:'_id'},
    { heading : "Name",value:'Name'},
    { heading : "Actions"}
  ]

  const bookColumns  =[
    { heading :"ID" ,value:'_id'},
    { heading : "Photo",value:'img'},
    { heading :"Name",value:'title'},
    { heading :"CategoryID",value:'cat_Id'},
    { heading :"AuthorID",value:'auth_ID'},
    { heading : "Actions"}
  ]
  const authorsColumn =[
    { heading :"ID" ,value:'_id'},
    { heading :"First Name",value:'fName'},
    { heading :"last Name",value:'lName'},
    { heading :"Date Of Birth",value:'DOB'},
    { heading : "Photo",value:'img'},
    { heading : "Actions"}
  ]

  useEffect(()=>{axios.get('http://localhost:3001/categories')
  .then(response => setCategoriesData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...categoriesData);

  useEffect(()=>{axios.get('http://localhost:3001/books')
  .then(response => setBooksData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...booksData);

  useEffect(()=>{axios.get('http://localhost:3001/authors')
  .then(response => setAuthorsData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...authorsData);


  const [categoryModalIsOpen, setCategoryModal] = useState(false);
  const categoryModalClose = () => setCategoryModal(false);
  const categoryModalShow = () => setCategoryModal(true);

  const [bookModalIsOpen, setBookModal] = useState(false);
  const bookModalClose = () => setBookModal(false);
  const bookModalShow = () => setBookModal(true);

  const [authorModalIsOpen, setAuthorModal] = useState(false);
  const authorModalClose = () => setAuthorModal(false);
  const authorModalShow = () => setAuthorModal(true);

  
    // const viewCategories=()=> {axios.get('http://localhost:3001/categories')
    //                           .then(response => {setData(response.data);})};

  return (
    <div className="">
      <Tabs
        defaultActiveKey="first"
        transition={false}
        id="threeTabs"
        className="tabs"
        >

  <Tab eventKey="first" title="Categories">
    <i className=" fa fa-plus fa-2x btn btn-success me-md-2 rounded-circle float-end" onClick={categoryModalShow}></i>
    < Table data={categoriesData} column={categoryColumns}/>
    {categoryModalIsOpen && <AddCategoryModal state={categoryModalIsOpen} onClick={categoryModalClose}/>}
  </Tab>

  <Tab eventKey="second" title="Books" >
    <i className="fa fa-plus fa-2x btn btn-success me-md-2 rounded-circle float-end" onClick={bookModalShow}></i>
    <Table data ={booksData} column={bookColumns}/>
    {bookModalIsOpen && <AddBookModal state={bookModalIsOpen} onClick={bookModalClose} categoryData={categoriesData}/>}
  </Tab>

  <Tab eventKey="third" title="Authors" >
    <i className="fa fa-plus fa-2x btn btn-success me-md-2 rounded-circle float-end" onClick={authorModalShow}></i>
    <Table data ={authorsData} column={authorsColumn}/>
    {authorModalIsOpen && <AddAuthorModal state={authorModalIsOpen} onClick={authorModalClose}/>}
    
  </Tab>
</Tabs>
    </div>
  );
}

export default ThreeTabs;
