// import bootstrapTable from 'react-bootstrap-table-next/lib/src/bootstrap-table';
// import BootstrapTable from 'react-bootstrap-table-next/lib/src/bootstrap-table';
// import axios from 'axios';

function CategoriesTable(probs){
    const data = probs.data;
    const columns = probs.column;
    // console.log(props);
    return (
        <div>
            <h1>Dynamic table</h1>
            <table className="table table-striped">
            <thead>
                <tr>
                {columns.map((item,index) =><TableHeadItem item={item}/>)}
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                        dddddd
                    </td>
                    <td>
                        eeeeeee
                    </td>
                </tr>
                {data.map((item,index) =><TableRow item={item} key={item['_id']} column={columns}/>)}
            </tbody>
            </table>
        </div>
    )
}
function TableHeadItem({item}){
    <th>{item.heading}</th>
    console.log(item.heading);
}
function TableRow({item , column}){
    // console.log(item['_id']);
    <tr key={item['_id']}>{column.map((columnItem , index)=> {
        console.log(item[`${columnItem.value}`]);
        return <td>{item[`${columnItem.value}`]}</td>
    })}
    </tr>
}
export default CategoriesTable;
