import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import { useFormik } from 'formik';

function AddCategoryModal(probs){

  const initialValues={
    newCategory:''
  }

  const onSubmit=values=>{
    console.log(values);
    probs.onClick();
  }
  const validate=values=>{
    let errors={};
    if(!values.newCategory){
      errors.newCategory= 'Category name must have value';
      // console.error('Category name is empty');
    }
    return errors;
  }
  const formik = useFormik({
    initialValues,
    onSubmit,
    validate
  })


  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add Category</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={formik.handleSubmit}>
            <Form.Group as={Row} className="mb-3" controlId="categoryName">
                <Col sm={3}>
                    <Form.Label>Category Name</Form.Label>
                </Col>
                <Col sm={9}>
                    <Form.Control
                        type="text"
                        name='newCategory'
                        // value={input.newCategory}
                        value={formik.values.newCategory}
                        autoComplete="off"
                        // onChange={handleChange}
                        onChange={formik.handleChange}
                        placeholder="Enter the Category Name"
                        autoFocus/>
                        {formik.errors.newCategory?(<span className='error'>{formik.errors.newCategory}</span>):null}
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" 
              // onClick={handleClick} 
              type="submit ">
                Add Category
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddCategoryModal;
