import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import React from 'react';
import { useState } from 'react';

function AddBookModal(probs){
  const [editedBook , setEditedCBook] = useState({
    bookName:'',
    category:'',
    author:'',
    image:''
  });
  const handleChange=(event)=>{
    const {name , value}=event.target;
    setEditedCBook(prevBook=>{
    return {
        ...prevBook,
        [name]:value
    }
    })
}
const handleClick=(event)=>{
    event.preventDefault();
    console.log(editedBook);
    const newBook = {
      Name:editedBook.bookName,
      Category:editedBook.category,
      Author:editedBook.author,
      image:editedBook.image
    }
    console.log(newBook);
    probs.onClick();
  }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title  className='justify-content-center'>Add Book</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group as={Row} className="mb-3" controlId="bookName">
                <Col sm={2}>
                    <Form.Label>Book Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        placeholder="Enter the Book Name"
                        name='bookName'
                        value={editedBook.bookName}
                        onChange={handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="category">
                <Col sm={2}>
                    <Form.Label>Category</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select>
                    <option></option>
                    {React.Children.toArray(probs.categriesData.map((item )=> {
                      return <option>{item['Name'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="author">
                <Col sm={2}>
                    <Form.Label>Author</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select>
                    <option></option>
                    {React.Children.toArray(probs.authorData.map((item )=> {
                      return <option>{item['fName'].toString() +' '+ item['lName'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        />
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" type="submit" onClick={handleClick}>
                Add Book
              </Button>
            </div>
          </Form>
        </Modal.Body>
        {/* <Modal.Footer>
          <Button variant="secondary" onClick={probs.onClick}>
            Close
          </Button>
          <Button variant="primary" onClick={probs.onClick}>
            Save Changes
          </Button>
        </Modal.Footer> */}
      </Modal>
    </div>
  );
}

export default AddBookModal;
