import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import { useFormik } from 'formik';

function EditCategoryModal(probs){

  //state of the input 
  const formik = useFormik({
    initialValues:{
      editedCategory:probs.item["Name"]
    },
    onSubmit:values=>{
      console.log(values);
      probs.onClick();
    }
  });

  // //to get the edited name of the category when the input value changes
  // const handleChange=(event)=>{
  //   const {name , value}=event.target;
  //   seteditedCategory(prevCategory=>{
  //     return {
  //       ...prevCategory,
  //       [name]:value
  //     }
  //   })
  // }

  //to edit the category in the database when the button is clicked
  // const handleClick=(event)=>{
  //   event.preventDefault();
  //   console.log(input);
  //   const editedCategory = {
  //     _id:probs.item["_id"],
  //     Name:input.editedCategory
  //   }
  //   console.log(editedCategory);
  //   probs.onClick();
    
  //   ///////// login to edit the specified category in the database
  // }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Edit Category</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={formik.handleSubmit}>
            <Form.Group as={Row} className="mb-3" controlId="categoryName">
                <Col sm={3}>
                    <Form.Label>Category Name</Form.Label>
                </Col>
                <Col sm={9}>
                    <Form.Control
                        type="text"
                        name='editedCategory'
                        // value={input.editedCategory}
                        autoComplete="off"
                        value={formik.values.editedCategory}
                        onChange={formik.handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" type="submit ">
                Edit Category
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EditCategoryModal;
