import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import React from 'react';
import { useState } from 'react';

function AddBookModal(probs){
  const [addedBook , setNewBook] = useState({
    bookName:'',
    category:'',
    author:'',
    image:''
  });
  const handleChange=(event)=>{
    const {name , value}=event.target;
    setNewBook(prevBook=>{
    return {
        ...prevBook,
        [name]:value
    }
    })
}
const handleClick=(event)=>{
    event.preventDefault();
    const newBook = {
      name:addedBook.bookName,
      category:addedBook.category,
      author:addedBook.author,
      image:addedBook.image
    }
    console.log(newBook);
    probs.onClick();
  }
  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title  className='justify-content-center'>Add Book</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group as={Row} className="mb-3" controlId="bookName">
                <Col sm={2}>
                    <Form.Label>Book Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        placeholder="Enter the Book Name"
                        autoComplete='off'
                        name='bookName'
                        value={addedBook.bookName}
                        onChange={handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="category">
                <Col sm={2}>
                    <Form.Label>Category</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='category'
                                     value={addedBook.category}
                                     onChange={handleChange}>
                    <option></option>
                    {React.Children.toArray(probs.categriesData.map((item )=> {
                      return <option>{item['Name'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="author">
                <Col sm={2}>
                    <Form.Label>Author</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='author'
                                     value={addedBook.author}
                                     onChange={handleChange}>
                    <option></option>
                    {React.Children.toArray(probs.authorData.map((item )=> {
                      return <option>{item['fName'].toString() +' '+ item['lName'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        />
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" type="submit" onClick={handleClick}>
                Add Book
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddBookModal;
