import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import React from 'react';
import { useFormik } from 'formik';

function AddBookModal(probs){

  const onSubmit=values=>{
    console.log(values);
    probs.onClick();
  }
  const validate=values=>{
    let errors={};
    if(!values.bookName){
      errors.bookName = 'Book Name must have value';
    }
    if(!values.category){
      errors.category = 'Category Name must have value';
    }
    if(!values.author){
      errors.author = 'Author must have value';
    }
    if(!values.image){
      errors.image = 'Please select book image'
    }
    return errors;
  }
  const formik = useFormik({
    initialValues,
    onSubmit,
    validate
    
    
  });

  return (
    <div>
      <Modal show={probs.state} onHide={probs.onClick} size="lg">
        <Modal.Header closeButton>
          <Modal.Title  className='justify-content-center'>Add Book</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={formik.handleSubmit}>
            <Form.Group as={Row} className="mb-3" controlId="bookName">
                <Col sm={2}>
                    <Form.Label>Book Name</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="text"
                        placeholder="Enter the Book Name"
                        autoComplete='off'
                        name='bookName'
                        // value={addedBook.bookName}
                        value ={formik.values.bookName}
                        onChange={formik.handleChange}
                        autoFocus/>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="category">
                <Col sm={2}>
                    <Form.Label>Category</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='category'
                                     value ={formik.values.category}
                                     onChange={formik.handleChange}>
                    <option></option>
                    {React.Children.toArray(probs.categriesData.map((item )=> {
                      return <option>{item['Name'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="author">
                <Col sm={2}>
                    <Form.Label>Author</Form.Label>
                </Col>
                <Col sm={10}>
                  <Form.Select name='author'
                                     value ={formik.values.author}
                                     onChange={formik.handleChange}>
                    <option></option>
                    {React.Children.toArray(probs.authorData.map((item )=> {
                      return <option>{item['fName'].toString() +' '+ item['lName'].toString()}</option>}))}
                  </Form.Select>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="image">
                <Col sm={2}>
                    <Form.Label>Image</Form.Label>
                </Col>
                <Col sm={10}>
                    <Form.Control
                        type="file"
                        />
                </Col>
            </Form.Group>
            <div className='text-center'>
              <Button variant="primary" type="submit">
                Add Book
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddBookModal;
