// import logo from './logo.svg';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import "../styles/ThreeTabs.css"
import Table from './Table';
import AuthorsTable from './AuthorsTabel';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';

function ThreeTabs() {
  const[categoriesData,setCategoriesData] = useState([]);
  const[booksData,setBooksData]=useState([]);
  const categoryColumns = [
    { heading :"ID",value:'_id'},
    { heading: "Name",value:'Name'},
    { heading: "Actions"}
  ]
  const bookColumns =[
    { heading :"ID" ,value:'_id'},
    { heading :"First Name",value:'fName'},
    { heading :"last Name",value:'fName'},
    { heading: "Photo",value:'img'},
    { heading: "Actions"}
  ]
  // const authorsColumn =[
  //   { heading :"ID" ,value:'_id'},
  //   { heading: "Photo",value:'img'},
  //   { heading :"Name",value:'title'},
  //   { heading :"CategoryID",value:'cat_Id'},
  //   { heading :"AuthorID",value:'auth_ID'},
  //   { heading: "Actions"}
  // ]
  useEffect(()=>{axios.get('http://localhost:3001/categories')
  .then(response => setCategoriesData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...categoriesData);

  useEffect(()=>{axios.get('http://localhost:3001/books')
  .then(response => setBooksData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...booksData);

  useEffect(()=>{axios.get('http://localhost:3001/authors')
  .then(response => setBooksData(response.data))
  .catch(err => console.log(err))},[])
  console.log(...booksData);

    // const viewCategories=()=> {axios.get('http://localhost:3001/categories')
    //                           .then(response => {setData(response.data);})};

  return (
    <div className="">
      <Tabs
        defaultActiveKey="first"
        transition={false}
        id="threeTabs"
        className="tabs"
        >

  <Tab eventKey="first" title="Categories">
    <button className='categoryPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
    < Table data={categoriesData} column={categoryColumns}/>
  </Tab>

  <Tab eventKey="second" title="Books" >
    <button className='booksPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
    <Table data ={booksData} column={bookColumns}/>
  </Tab>

  <Tab eventKey="third" title="Authors" >
    <button className='authorsPlusBtn btn btn-success me-md-2 rounded-circle'><i className="fa fa-plus"></i></button>
    < AuthorsTable/>
  </Tab>
</Tabs>
    </div>
  );
}

export default ThreeTabs;
